const extractProperties = (item) => {
  if (!item || Array.isArray(item)) {
    return {};
  }
  if (item.properties) {
    return extractProperties(item.properties);
  }
  if (item.attributes) {
    return extractProperties(item.attributes);
  }
  if (item.fields) {
    return extractProperties(item.fields);
  }
  return item;
};

export default extractProperties;

const googlePoint = (item, noMandatory) => {
  if (!item) {
    throw new Error('Geometry Param is mandatory');
  }
  if (Array.isArray(item)) {
    return { lat: item[0], lng: item[1] };
  }
  if (item.lat && item.lng) {
    return item;
  }
  if (item.lat && item.lon) {
    return { lat: item.lat, lng: item.lon };
  }
  if (item.latitude && item.longitude) {
    return { lat: item.latitude, lng: item.longitude };
  }
  if (item.latitud && item.longitud) {
    return { lat: item.latitud, lng: item.longitud };
  }
  if (item.position) {
    return googlePoint(item.position);
  }
  if (item.location) {
    return googlePoint(item.location);
  }
  if (item.center) {
    return googlePoint(item.center);
  }
  if (noMandatory) {
    return null;
  }
  throw new Error('Geometry is mandatory');
};

export default googlePoint;

const leafletPoint = (item, noMandatory) => {
  if (!item) {
    throw new Error('Geometry Param is mandatory');
  }
  if (Array.isArray(item)) {
    return item;
  }
  if (item.lat && item.lng) {
    return [item.lat, item.lng];
  }
  if (item.lat && item.lon) {
    return [item.lat, item.lon];
  }
  if (item.latitude && item.longitude) {
    return [item.latitude, item.longitude];
  }
  if (item.latitud && item.longitud) {
    return [item.latitud, item.longitud];
  }
  if (item.position) {
    return leafletPoint(item.position);
  }
  if (item.location) {
    return leafletPoint(item.location);
  }
  if (item.center) {
    return leafletPoint(item.center);
  }
  if (noMandatory) {
    return null;
  }
  throw new Error('Geometry is mandatory');
};

